import styled from 'styled-components';

const AppContainer = styled.div`
    padding-top: 80px; /* Ensure content is not hidden behind the header */
    padding-bottom: 60px; /* Ensure content is not hidden behind the footer */
    min-height: 100vh;
    background-color: #f0f2f5;
`;

export default AppContainer;
