import React, { useState, useEffect, useRef } from 'react';
import api from '../services/api';

function OpinionSubmission() {
    const [plans, setPlans] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState('');
    const [voiceNote, setVoiceNote] = useState(null);
    const [isRecording, setIsRecording] = useState(false);
    const [audioUrl, setAudioUrl] = useState('');
    const mediaRecorderRef = useRef(null);
    const audioChunksRef = useRef([]);

    useEffect(() => {
        fetchPlans();
    }, []);

    const fetchPlans = async () => {
        try {
            const response = await api.get('/plans/');
            setPlans(response.data);
        } catch (error) {
            console.error('Error fetching plans', error);
        }
    };

    const startRecording = async () => {
        try {
            const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
            mediaRecorderRef.current = new MediaRecorder(stream);
            mediaRecorderRef.current.ondataavailable = event => {
                audioChunksRef.current.push(event.data);
            };
            mediaRecorderRef.current.onstop = () => {
                const audioBlob = new Blob(audioChunksRef.current, { type: 'audio/wav' });
                const audioUrl = URL.createObjectURL(audioBlob);
                setAudioUrl(audioUrl);
                setVoiceNote(audioBlob);
                audioChunksRef.current = [];
            };
            mediaRecorderRef.current.start();
            setIsRecording(true);
        } catch (error) {
            console.error('Error starting recording', error);
        }
    };

    const stopRecording = () => {
        if (mediaRecorderRef.current) {
            mediaRecorderRef.current.stop();
            setIsRecording(false);
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!selectedPlan || !voiceNote) {
            alert('Please select a plan and record a voice note.');
            return;
        }

        const formData = new FormData();
        formData.append('plan', selectedPlan);
        formData.append('voice_note', voiceNote);

        try {
            const response = await api.post('/opinions/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            if (response.status === 201) {
                alert('Opinion submitted successfully!');
                setSelectedPlan('');
                setVoiceNote(null);
                setAudioUrl('');
            } else {
                alert('Failed to submit opinion.');
            }
        } catch (error) {
            console.error('Error submitting opinion', error);
            alert('Error submitting opinion.');
        }
    };

    return (
        <div>
            <h2>Submit Your Opinion</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Select Plan:
                    <select value={selectedPlan} onChange={(e) => setSelectedPlan(e.target.value)}>
                        <option value="">Select a plan</option>
                        {plans.map(plan => (
                            <option key={plan.id} value={plan.id}>{plan.title}</option>
                        ))}
                    </select>
                </label>
                <br />
                <div>
                    <button type="button" onClick={startRecording} disabled={isRecording}>
                        Start Recording
                    </button>
                    <button type="button" onClick={stopRecording} disabled={!isRecording}>
                        Stop Recording
                    </button>
                </div>
                {audioUrl && (
                    <div>
                        <h3>Playback</h3>
                        <audio controls src={audioUrl}></audio>
                    </div>
                )}
                <button type="submit">Submit Opinion</button>
            </form>
        </div>
    );
}

export default OpinionSubmission;
