import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';

function PlanManagement() {
    const [plans, setPlans] = useState([]);
    const [title, setTitle] = useState('');
    const [description, setDescription] = useState('');
    const [editingPlan, setEditingPlan] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        fetchUserDetails();
        fetchPlans();
    }, []);

    const fetchUserDetails = async () => {
        try {
            const response = await api.get('/me/');
            if (!response.data.is_staff) {
                navigate('/dashboard'); // Redirect non-admin users to dashboard
            }
        } catch (error) {
            console.error('Error fetching user details', error);
        }
    };

    const fetchPlans = async () => {
        try {
            const response = await api.get('/plans/');
            setPlans(response.data);
        } catch (error) {
            console.error('Error fetching plans', error);
        }
    };

    const handleCreateOrUpdatePlan = async () => {
        try {
            if (editingPlan) {
                await api.put(`/plans/${editingPlan.id}/`, { title, description });
                setPlans(plans.map(plan => (plan.id === editingPlan.id ? { ...plan, title, description } : plan)));
            } else {
                const response = await api.post('/plans/', { title, description });
                setPlans([...plans, response.data]);
            }
            setTitle('');
            setDescription('');
            setEditingPlan(null);
        } catch (error) {
            console.error(`Error ${editingPlan ? 'updating' : 'creating'} plan`, error);
        }
    };

    const handleEditPlan = (plan) => {
        setTitle(plan.title);
        setDescription(plan.description);
        setEditingPlan(plan);
    };

    const handleDeletePlan = async (id) => {
        try {
            await api.delete(`/plans/${id}/`);
            setPlans(plans.filter(plan => plan.id !== id));
        } catch (error) {
            console.error('Error deleting plan', error);
        }
    };

    return (
        <div>
            <h2>Manage Plans</h2>
            <div>
                <input
                    type="text"
                    value={title}
                    onChange={(e) => setTitle(e.target.value)}
                    placeholder="Title"
                />
                <textarea
                    value={description}
                    onChange={(e) => setDescription(e.target.value)}
                    placeholder="Description"
                ></textarea>
                <button onClick={handleCreateOrUpdatePlan}>
                    {editingPlan ? 'Update Plan' : 'Create Plan'}
                </button>
            </div>
            <ul>
                {plans.map(plan => (
                    <li key={plan.id}>
                        <h4>{plan.title}</h4>
                        <p>{plan.description}</p>
                        <button onClick={() => handleEditPlan(plan)}>Edit</button>
                        <button onClick={() => handleDeletePlan(plan.id)}>Delete</button>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default PlanManagement;
