import React, { useState, useEffect, useRef } from 'react';
import { FaMicrophone, FaStop, FaPlay } from 'react-icons/fa';
import api from '../services/api';

function OpinionSubmission() {
    const [plans, setPlans] = useState([]);
    const [selectedPlan, setSelectedPlan] = useState('');
    const [recording, setRecording] = useState(false);
    const [audioUrl, setAudioUrl] = useState('');
    const [audioBlob, setAudioBlob] = useState(null);
    const mediaRecorderRef = useRef(null);
    const audioRef = useRef(null);

    useEffect(() => {
        fetchPlans();
    }, []);

    const fetchPlans = async () => {
        try {
            const response = await api.get('/plans/');
            setPlans(response.data);
        } catch (error) {
            console.error('Error fetching plans', error);
        }
    };

    const startRecording = async () => {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        mediaRecorderRef.current = new MediaRecorder(stream);
        mediaRecorderRef.current.ondataavailable = event => {
            const audioChunks = [event.data];
            const blob = new Blob(audioChunks, { type: 'audio/wav' });
            const url = URL.createObjectURL(blob);
            setAudioBlob(blob);
            setAudioUrl(url);
        };
        mediaRecorderRef.current.start();
        setRecording(true);
    };

    const stopRecording = () => {
        mediaRecorderRef.current.stop();
        setRecording(false);
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!selectedPlan || !audioBlob) {
            alert('Please select a plan and record a voice note.');
            return;
        }

        const formData = new FormData();
        formData.append('plan', selectedPlan);
        formData.append('voice_note', audioBlob, 'voice_note.wav');

        try {
            const response = await api.post('/opinions/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data'
                }
            });
            if (response.status === 201) {
                alert('Opinion submitted successfully!');
                setSelectedPlan('');
                setAudioUrl('');
                setAudioBlob(null);
            } else {
                alert('Failed to submit opinion.');
            }
        } catch (error) {
            console.error('Error submitting opinion', error);
            alert('Error submitting opinion.');
        }
    };

    return (
        <div>
            <h2>Submit Your Opinion</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Select Plan:
                    <select value={selectedPlan} onChange={(e) => setSelectedPlan(e.target.value)}>
                        <option value="">Select a plan</option>
                        {plans.map(plan => (
                            <option key={plan.id} value={plan.id}>{plan.title}</option>
                        ))}
                    </select>
                </label>
                <br />
                <div>
                    {!recording ? (
                        <button type="button" onClick={startRecording}>
                            <FaMicrophone /> Start Recording
                        </button>
                    ) : (
                        <button type="button" onClick={stopRecording}>
                            <FaStop /> Stop Recording
                        </button>
                    )}
                </div>
                {audioUrl && (
                    <div>
                        <h3>Preview:</h3>
                        <audio controls src={audioUrl} ref={audioRef} />
                    </div>
                )}
                <button type="submit">Submit Opinion</button>
            </form>
        </div>
    );
}

export default OpinionSubmission;
