import React, { useState, useEffect, useRef, useCallback } from 'react';
import styled from 'styled-components';
import axios from 'axios';
import { useParams } from 'react-router-dom'; 
import { FaMicrophone, FaStop } from 'react-icons/fa';

const Container = styled.div`
    padding: 20px;
    background-color: #f0f2f5;
    min-height: 100vh;
`;

const Title = styled.h2`
    font-size: 24px;
    color: #333;
    margin-bottom: 20px;
`;

const Button = styled.button`
    width: 100%;
    padding: 10px;
    border: none;
    border-radius: 4px;
    background-color: #007bff;
    color: white;
    font-size: 16px;
    cursor: pointer;
    transition: background-color 0.3s;
    margin-top: 10px;
    &:hover {
        background-color: #0056b3;
    }
`;

const AudioPreview = styled.div`
    margin-top: 20px;
`;

const List = styled.ul`
    list-style-type: none;
    padding: 0;
    margin-top: 20px;
`;

const ListItem = styled.li`
    margin-bottom: 15px;
    background-color: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    padding: 15px;
`;

function Opinions() {
    const { planId } = useParams();
    const [opinions, setOpinions] = useState([]);
    const [recording, setRecording] = useState(false);
    const [audioUrl, setAudioUrl] = useState('');
    const [audioBlob, setAudioBlob] = useState(null);
    const mediaRecorderRef = useRef(null);
    const audioRef = useRef(null);

    const fetchOpinions = useCallback(async () => {
        try {
            const response = await axios.get(`http://localhost:8000/api/opinions/?plan=${planId}`, {
                headers: {
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });
            setOpinions(response.data);
        } catch (error) {
            console.error('Error fetching opinions', error);
        }
    }, [planId]);

    useEffect(() => {
        fetchOpinions();
    }, [fetchOpinions]);

    const startRecording = async () => {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        mediaRecorderRef.current = new MediaRecorder(stream);
        mediaRecorderRef.current.ondataavailable = event => {
            const audioChunks = [event.data];
            const blob = new Blob(audioChunks, { type: 'audio/wav' });
            const url = URL.createObjectURL(blob);
            setAudioBlob(blob);
            setAudioUrl(url);
        };
        mediaRecorderRef.current.start();
        setRecording(true);
    };

    const stopRecording = () => {
        mediaRecorderRef.current.stop();
        setRecording(false);
    };

    const handleUpload = async () => {
        const formData = new FormData();
        formData.append('voice_note', audioBlob);
        formData.append('plan', planId);

        try {
            await axios.post('http://localhost:8000/api/opinions/', formData, {
                headers: {
                    'Content-Type': 'multipart/form-data',
                    Authorization: `Bearer ${localStorage.getItem('token')}`
                }
            });
            fetchOpinions();
            setAudioUrl('');
            setAudioBlob(null);
        } catch (error) {
            console.error('Error uploading voice note', error);
        }
    };

    return (
        <Container>
            <Title>Opinions for Plan {planId}</Title>
            <Button onClick={handleUpload}>Upload Voice Note</Button>
            <div>
                {!recording ? (
                    <Button type="button" onClick={startRecording}>
                        <FaMicrophone /> Start Recording
                    </Button>
                ) : (
                    <Button type="button" onClick={stopRecording}>
                        <FaStop /> Stop Recording
                    </Button>
                )}
            </div>
            {audioUrl && (
                <AudioPreview>
                    <h3>Preview:</h3>
                    <audio controls src={audioUrl} ref={audioRef} />
                </AudioPreview>
            )}
            <List>
                {opinions.map(opinion => (
                    <ListItem key={opinion.id}>
                        <audio controls src={opinion.voice_note} />
                    </ListItem>
                ))}
            </List>
        </Container>
    );
}

export default Opinions;
