import React, { useEffect, useState } from 'react';
import api from '../services/api';
import styled from 'styled-components';

const Container = styled.div`
    padding: 20px;
    background-color: #f0f2f5;
    min-height: 100vh;
`;

const Section = styled.div`
    background-color: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    padding: 20px;
    margin-bottom: 20px;
`;

const Title = styled.h2`
    font-size: 24px;
    color: #333;
    margin-bottom: 20px;
`;

const SubTitle = styled.h3`
    font-size: 20px;
    color: #333;
    margin-bottom: 15px;
`;

const List = styled.ul`
    list-style-type: none;
    padding: 0;
`;

const ListItem = styled.li`
    margin-bottom: 15px;
`;

const PlanTitle = styled.h4`
    font-size: 18px;
    color: #007bff;
    margin: 0;
`;

const PlanDescription = styled.p`
    font-size: 16px;
    color: #555;
`;

const OpinionText = styled.p`
    font-size: 16px;
    color: #555;
`;

function Dashboard() {
    const [user, setUser] = useState(null);
    const [plans, setPlans] = useState([]);
    const [opinions, setOpinions] = useState([]);

    useEffect(() => {
        fetchUserDetails();
        fetchPlans();
        fetchOpinions();
    }, []);

    const fetchUserDetails = async () => {
        try {
            const response = await api.get('/me/');
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user details', error);
        }
    };

    const fetchPlans = async () => {
        try {
            const response = await api.get('/plans/');
            setPlans(response.data);
        } catch (error) {
            console.error('Error fetching plans', error);
        }
    };

    const fetchOpinions = async () => {
        try {
            const response = await api.get('/opinions/');
            setOpinions(response.data);
        } catch (error) {
            console.error('Error fetching opinions', error);
        }
    };

    return (
        <Container>
            <Title>User Dashboard</Title>
            {user && (
                <Section>
                    <SubTitle>User Details</SubTitle>
                    <p>Username: {user.username}</p>
                    <p>Email: {user.email}</p>
                </Section>
            )}
            <Section>
                <SubTitle>Urban Planning Projects</SubTitle>
                <List>
                    {plans.map(plan => (
                        <ListItem key={plan.id}>
                            <PlanTitle>{plan.title}</PlanTitle>
                            <PlanDescription>{plan.description}</PlanDescription>
                        </ListItem>
                    ))}
                </List>
            </Section>
            <Section>
                <SubTitle>Your Opinions</SubTitle>
                <List>
                    {opinions.map(opinion => (
                        <ListItem key={opinion.id}>
                            <PlanTitle>On Plan: {opinion.plan.title}</PlanTitle>
                            <OpinionText>{opinion.transcribed_text}</OpinionText>
                        </ListItem>
                    ))}
                </List>
            </Section>
        </Container>
    );
}

export default Dashboard;
