import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from 'styled-components';
import api from '../services/api';

const Container = styled.div`
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: flex-start;
    min-height: 100vh;
    background: linear-gradient(135deg, #f0f2f5, #c9d6ff);
    padding: 40px 20px;
`;

const Box = styled.div`
    width: 100%;
    max-width: 900px;
    background-color: #fff;
    border-radius: 12px;
    box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
    padding: 30px;
    margin-bottom: 30px;
`;

const Title = styled.h2`
    font-size: 28px;
    color: #333;
    margin-bottom: 20px;
    font-weight: 700;
    text-transform: uppercase;
    text-align: center;
`;

const SubTitle = styled.h3`
    font-size: 22px;
    color: #007bff;
    margin-bottom: 15px;
    text-transform: uppercase;
`;

const UserDetails = styled.div`
    font-size: 18px;
    color: #555;
    margin-bottom: 20px;
    p {
        margin: 5px 0;
    }
`;

const List = styled.ul`
    list-style-type: none;
    padding: 0;
    margin: 0;
`;

const ListItem = styled.li`
    margin-bottom: 15px;
    background-color: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    padding: 15px;
    cursor: pointer;
    transition: transform 0.3s, box-shadow 0.3s;
    &:hover {
        transform: translateY(-2px);
        box-shadow: 0 8px 16px rgba(0, 0, 0, 0.2);
    }
`;

const PlanTitle = styled.h4`
    font-size: 20px;
    color: #007bff;
    margin: 0;
`;

const PlanDescription = styled.p`
    font-size: 16px;
    color: #555;
`;

const Button = styled.button`
    padding: 10px 20px;
    border: none;
    border-radius: 6px;
    background-color: #007bff;
    color: white;
    font-size: 16px;
    font-weight: 600;
    cursor: pointer;
    transition: background-color 0.3s, transform 0.3s;
    margin-right: 10px;
    &:hover {
        background-color: #0056b3;
        transform: translateY(-2px);
    }
`;

const AddOpinionButton = styled(Button)`
    background-color: #28a745;
    &:hover {
        background-color: #218838;
    }
`;

function Dashboard() {
    const [user, setUser] = useState(null);
    const [plans, setPlans] = useState([]);
    const [opinions, setOpinions] = useState([]);
    const navigate = useNavigate();

    useEffect(() => {
        fetchUserDetails();
        fetchPlans();
        fetchOpinions();
    }, []);

    const fetchUserDetails = async () => {
        try {
            const response = await api.get('/me/');
            setUser(response.data);
        } catch (error) {
            console.error('Error fetching user details', error);
        }
    };

    const fetchPlans = async () => {
        try {
            const response = await api.get('/plans/');
            setPlans(response.data);
        } catch (error) {
            console.error('Error fetching plans', error);
        }
    };

    const fetchOpinions = async () => {
        try {
            const response = await api.get('/opinions/');
            setOpinions(response.data);
        } catch (error) {
            console.error('Error fetching opinions', error);
        }
    };

    const handlePlanClick = (planId) => {
        navigate(`/plans/${planId}`);
    };

    const handleAddOpinion = (planId) => {
        navigate(`/opinions/${planId}`);
    };

    return (
        <Container>
            <Title>User Dashboard</Title>
            {user && (
                <Box>
                    <SubTitle>User Details</SubTitle>
                    <UserDetails>
                        <p><strong>Username:</strong> {user.username}</p>
                        <p><strong>Email:</strong> {user.email}</p>
                        {user.is_staff && <Button onClick={() => navigate('/manage-plans')}>Manage Plans</Button>}
                    </UserDetails>
                </Box>
            )}
            <Box>
                <SubTitle>Urban Planning Projects</SubTitle>
                <List>
                    {plans.map(plan => (
                        <ListItem key={plan.id} onClick={() => handlePlanClick(plan.id)}>
                            <PlanTitle>{plan.title}</PlanTitle>
                            <PlanDescription>{plan.description}</PlanDescription>
                            {!user.is_staff && (
                                <AddOpinionButton onClick={(e) => { e.stopPropagation(); handleAddOpinion(plan.id); }}>
                                    Add Opinion
                                </AddOpinionButton>
                            )}
                        </ListItem>
                    ))}
                </List>
            </Box>
            <Box>
                <SubTitle>Your Opinions</SubTitle>
                <List>
                    {opinions.map(opinion => (
                        <ListItem key={opinion.id}>
                            <PlanTitle>On Plan: {opinion.plan.title}</PlanTitle>
                            <PlanDescription>{opinion.transcribed_text}</PlanDescription>
                        </ListItem>
                    ))}
                </List>
            </Box>
        </Container>
    );
}

export default Dashboard;
