import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import styled from 'styled-components';
import api from '../services/api';

const Container = styled.div`
    padding: 20px;
    background-color: #f0f2f5;
    min-height: 100vh;
`;

const Section = styled.div`
    background-color: #fff;
    border-radius: 8px;
    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.1);
    padding: 20px;
    margin-bottom: 20px;
`;

const Title = styled.h2`
    font-size: 24px;
    color: #333;
    margin-bottom: 20px;
`;

const PlanDescription = styled.p`
    font-size: 16px;
    color: #555;
`;

const OpinionSection = styled.div`
    margin-top: 20px;
`;

const OpinionItem = styled.div`
    background-color: #f9f9f9;
    padding: 15px;
    margin-bottom: 15px;
    border-radius: 8px;
`;

const ReplyForm = styled.form`
    margin-top: 10px;
`;

const ReplyInput = styled.textarea`
    width: 100%;
    padding: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    margin-bottom: 10px;
`;

const Button = styled.button`
    padding: 10px 15px;
    background-color: #007bff;
    color: white;
    border: none;
    border-radius: 4px;
    cursor: pointer;
`;

function PlanDetails() {
    const { planId } = useParams();
    const [plan, setPlan] = useState(null);
    const [opinions, setOpinions] = useState([]);
    const [replyText, setReplyText] = useState('');
    const [selectedOpinionId, setSelectedOpinionId] = useState(null);

    useEffect(() => {
        const fetchPlanDetails = async () => {
            try {
                const response = await api.get(`/plans/${planId}/`);
                setPlan(response.data);
            } catch (error) {
                console.error('Error fetching plan details', error);
            }
        };

        const fetchOpinions = async () => {
            try {
                const response = await api.get(`/opinions/?plan=${planId}`);
                setOpinions(response.data);
            } catch (error) {
                console.error('Error fetching opinions', error);
            }
        };

        fetchPlanDetails();
        fetchOpinions();
    }, [planId]);

    const handleReplySubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await api.post(`/opinions/${selectedOpinionId}/reply/`, {
                reply_text: replyText,
            });
            setOpinions((prevOpinions) => prevOpinions.map(opinion =>
                opinion.id === selectedOpinionId
                    ? { ...opinion, replies: [...opinion.replies, response.data] }
                    : opinion
            ));
            setReplyText('');
            setSelectedOpinionId(null);
        } catch (error) {
            console.error('Error submitting reply', error);
        }
    };

    return (
        <Container>
            {plan ? (
                <Section>
                    <Title>{plan.title}</Title>
                    <PlanDescription>{plan.description}</PlanDescription>
                    <OpinionSection>
                        <h3>Opinions</h3>
                        {opinions.length > 0 ? (
                            opinions.map(opinion => (
                                <OpinionItem key={opinion.id}>
                                    <p><strong>{opinion.citizen}:</strong> {opinion.transcribed_text || "No transcription available."}</p>
                                    <audio controls src={opinion.voice_note} />
                                    {opinion.replies && opinion.replies.length > 0 && (
                                        <div>
                                            <h4>Replies:</h4>
                                            {opinion.replies.map(reply => (
                                                <p key={reply.id}><strong>{reply.replied_by}:</strong> {reply.reply_text}</p>
                                            ))}
                                        </div>
                                    )}
                                    {plan.created_by === localStorage.getItem('username') && (
                                        <ReplyForm onSubmit={handleReplySubmit}>
                                            <ReplyInput
                                                value={replyText}
                                                onChange={(e) => {
                                                    setReplyText(e.target.value);
                                                    setSelectedOpinionId(opinion.id);
                                                }}
                                                placeholder="Write a reply..."
                                            />
                                            <Button type="submit">Reply</Button>
                                        </ReplyForm>
                                    )}
                                </OpinionItem>
                            ))
                        ) : (
                            <p>No opinions yet.</p>
                        )}
                    </OpinionSection>
                </Section>
            ) : (
                <p>Loading...</p>
            )}
        </Container>
    );
}

export default PlanDetails;
